mapper.insert:
insert into sort (sortid, sortname, cateid
  )
values (#{sortid,jdbcType=INTEGER}, #{sortname,jdbcType=VARCHAR}, #{cateid,jdbcType=INTEGER}
  )

mapper.update:
update sort
set sortname = #{sortname,jdbcType=VARCHAR},
  cateid = #{cateid,jdbcType=INTEGER}
where sortid = #{sortid,jdbcType=INTEGER}
