mapper.update:
update cart
set modid = #{modid,jdbcType=INTEGER},
  commquantity = #{commquantity,jdbcType=INTEGER},
  purid = #{purid,jdbcType=INTEGER}
where crid = #{crid,jdbcType=INTEGER}

mapper.select:
    select *
    from cart


		and modid=#{modid}


		and purid=#{purid}

select a.crid,a.commquantity,a.purid,a.modid,b.commname,b.commpicture1 as commpicture ,b.modname,b.modprice,a.commquantity*b.modprice as jiage from cart a left join 
(select a.*,b.commname,b.commpicture1 from model a left join commodity b on a.commid=b.commid ) as b on a.modid=b.modid


		and purid=#{purid}
